import { useState } from 'react'
import './CardDescription.scss'

export const CardDescription = (props) => {
    const [description,setDescription] = useState(props.cardToSave.description)
    const [newCardToSave,setNewCardToSave] = useState(props.cardToSave.description)

    const updateDescription = (e) => {
        setDescription(e.target.value)
    }

    return (
        <div className="description-container item-container">
            <label htmlFor="description"> <span><i className="fas fa-align-right"></i> </span> Description</label>
            <main>
                <textarea name="description"  id="description" cols="30" rows="10" placeholder="Description..." value={description} onChange={updateDescription}></textarea>
                <button className="save-button submit-button-trello" onClick={((e) => props.updateCard(e,props.cardToSave))} >SAVE</button>
            </main>
        </div>
    )
}
