import { useEffect } from 'react';
import { listService } from '../../services/board.service'
import { useDispatch } from 'react-redux';
import { loadBaords, saveBoard } from '../../store/actions/BoardActions'
import {FormButton} from '../FormButton'

import './NewListButton.scss'

export const NewListButton = (props) => {

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(loadBaords())
    }, []);

    const onAddNewList = (newList) => {
        dispatch(saveBoard({...listService.getEmptyList(), ...newList }))
    }

    return (
        <section className="new-list-button-container">
            <FormButton itemType="list" onSubmit={onAddNewList} />
        </section>
    )
}
