    const boards = useSelector(state => state.BoardReducer.boards)
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(loadBoards())
    }, []);
    return (
        <section className="board-details">
            {boards && <ListsContainer lists={lists}/>}
        </section>
    )
}
